import React from 'react';
import axios from 'axios';

// const db = "database placehlder"

class ImageUpload extends React.Component {

  constructor(props){
     super(props);
     this.state = {}
   }



  showWidget = () => {
    const url = 'http://localhost:8000/';

    let widget = window.cloudinary.createUploadWidget({
       cloudName: `luisan`,
       uploadPreset: `jufwcv6o`},
        (error, result) => {
            if (!error && result && result.event === "success") {
                console.log(result.info.url);
                const data = {
                    peg: this.props.peg,
                    imageURL: result.info.url
                }
                axios
                    .post(url+'postData', data)
                    .then(res => console.log(res))
                    .catch(err => {
                        console.log(err)
                        image='https://apod.nasa.gov/apod/image/2001/NGC247hager_1024.jpg';
                    })
            }
    //   db.collection("images").doc(1).set({
    //     imgUrl: result.info.url,
    //     pegNumber: 1,
    // })
    // .then(function() {
    //     console.log("Document successfully written!");
    // })
    // .catch(function(error) {
    //     console.error("Error writing document: ", error);
    // });
        }
    );
    widget.open()
  }
  render() {
    return (<div>
            <script src="https://widget.cloudinary.com/v2.0/global/all.js" type="text/javascript">  
    </script>
           <button onPointerDown={this.showWidget}> Upload Image </button>
           </div>
    );
  }
}
export default ImageUpload;
