import React, { useState, useEffect } from 'react';
import ImageUpload from './ImageUpload';
import axios from 'axios';


const OptionEditable = ({...props}) => {
    const { id } = props;
    const [image, setImage] = useState('https://apod.nasa.gov/apod/image/2001/22466-22467anaVantuyne900.jpg')
    //placeholder image
    let pegName="add a name to this peg";
    let pegNumberStr = id.toString();

    pegNumberStr = id>99 ? pegNumberStr.slice(1) : pegNumberStr;

    useEffect(() => {
        get({ "peg": pegNumberStr })
    })

    const url = 'http://localhost:8000/';
    const get = (data) => {
        axios
            .get(url+'getImageUrl', {
                params: data
            })
            .then(res => setImage(res.data.data[0].imageURL))
            .catch(err => console.log(err))
    }

    return (
        <>
            <div class="option-card" onPointerDown={() => "" }>
                <h1>{pegName}</h1>
                <form onSubmit={(e) => {e.preventDefault(); console.log("theologians")}}>
                    <input type='text' />
                </form>
                <img src={image} style={{height: '100px', width: '100px', border: '2px solid black', background: 'lightgray'}} />
                <h4>{pegNumberStr}</h4>
                <ImageUpload peg={id} />
            </div>
        </>
        )
}

export default OptionEditable
