import React, { useState, useEffect } from 'react';
import axios from 'axios';
// const express = require('express')
// const cloudinary = require('cloudinary')
// const formData = require('express-form-data')
// const cors = require('cors')


const Option = ({...props}) => {
    const {answer, id, sendAnswer} = props;

    let answerStr = answer.toString();
    answerStr = answer>99 ? answerStr.slice(1) : answerStr;
    // const url = 'https://firebasestorage.googleapis.com/v0/b/memory-pegs.appspot.com/o/Goofy.jpg?alt=media&token=dd6d5629-1bea-449c-9104-e182a1e571fe'
    const url = 'http://localhost:8000/';
    const get = () => {
        axios
            .get(url)
            .then(res => console.log(res))
            .catch(err => console.log(err))
    }
    return (
        <>
            <div onPointerDown={() => sendAnswer(answer)}>
            <h1>{answerStr}</h1>
                Send me as answer!
                {/* <img src={url} /> */}
            </div>
            <div onPointerDown={() => get()} >
                GET REQUEST TO SERVER
            </div>
        </>
        )
}

export default Option
