import React from 'react';
import axios from 'axios';

// const db = "database placehlder"

class ImageUpload extends React.Component {

  constructor(props){
     super(props);
     this.state = {}
   }



  showWidget = () => {
    const url = 'http://localhost:8000/';

    let widget = window.cloudinary.createUploadWidget({
       cloudName: `luisan`,
       uploadPreset: `jufwcv6o`},
        (error, result) => {
            if (!error && result && result.event === "success") {
                console.log(result.info.url);
                const data = {
                    peg: this.props.peg,
                    imageURL: result.info.url,
                    pegName: this.props.pegName
                }
                axios
                    .post(url+'postData', data)
                    .then(res => this.props.update())
                    .catch(err => {
                        console.log(err)
                    })
            }
        }
    );
    widget.open()
  }
  render() {
    // console.log("img upload:", this.props);
    return (<div>
            <script src="https://widget.cloudinary.com/v2.0/global/all.js" type="text/javascript">  
    </script>
           <button onPointerDown={this.showWidget}> Upload Image </button>
           </div>
    );
  }
}
export default ImageUpload;
