import React, { useState, useEffect } from 'react';


const Option = ({...props}) => {
    const [optionsAmount, setOptionsAmount] = useState(4);
    const {answer, id, sendAnswer} = props;
    console.log("SINGLE OPTION:", sendAnswer);

    let answerStr = answer.toString();
    answerStr = answer>99 ? answerStr.slice(1) : answerStr;
    sendAnswer(3)

    return (
        <>
            <h1>Option with image number {answerStr}</h1>
            <div onClick={() => sendAnswer(answer)}>
                Send me as answer!
            </div>
        </>
        )
}

export default Option
