import React from 'react';
import Option from './Option'

const Options = ({...props}) => {
    createOptions = (optionsAmount) => {
        let options = [];
        for (let i=0; i<optionsAmount; i++) {
            options.push(<Option id={i} />)
        }
    }
    return (

        <>
            {this.createOptions()}
        </>
        
        )
}

export default Options
