import React from 'react';
import Option from './Option'

const Options = ({...props}) => {
    const {optionsAmount, answer} = props;
    const createOptions = optionsAmount => {
        let options = [];
        let randomNumber = Math.round(Math.random()*111)
        for (let i=0; i<optionsAmount-1; i++) {
            do {
                randomNumber = Math.round(Math.random()*111);
            } while (randomNumber === answer)

            options.push(<Option id={randomNumber} answer={randomNumber}/>)
        }
        console.log("answer:", answer);
        options.push(<Option id={answer} answer={answer}/>)
        console.log(options);
        //shuffle options order
        return options
    }
    return (

        <>
            {createOptions(optionsAmount)}
        </>
        
        )
}

export default Options
