import React from 'react';
import Option from './Option'

const Options = ({...props}) => {
    const {optionsAmount} = props;
    console.log(optionsAmount);
    const createOptions = optionsAmount => {
        console.log("inside:", optionsAmount);
        let options = [];
        for (let i=0; i<optionsAmount; i++) {
            console.log("i:", i);
            options.push("<Option id={i} />")
        }
        console.log(options);
        return options
    }
    return (

        <>
            {createOptions(optionsAmount)}
        </>
        
        )
}

export default Options
